import React from 'react';
import {TouchableOpacity,TextInput,KeyboardAvoidingView,FlatList,StyleSheet,Text,View,Image} from 'react-native';
import {f,auth,database,storage} from '../../config/config';


class comment extends React.Component{

    constructor(props){
        super(props);
        this.state = {
            loggedin: false,
            comments_list:[]
        }
        
    }

    checkParams= () => {


    }


    s4 = () => {
        return Math.floor((1 + Math.random()) * 0x10000)
          .toString(16)
          .substring(1);
      };
      uniqueId = () => {
        return (
          this.s4() + this.s4() + "-" + this.s4() + "-" + this.s4() + "-" + this.s4() + "-" + this.s4() + "-" + this.s4() + "-" + this.s4()
        );
      };
    pluralCheck = (s) => {
        if(s==1){
            return ' ago';
        }else{
            return 's ago';
        }
    }
    timeConverter = (timestamp)=> {
        var a = new Date(timestamp *1000);
        var seconds  =Math.floor((new Date()-a)/1000);

        var interval = Math.floor(seconds / 3153600);
        if(interval>1){
            return interval+ 'year'+this.pluralCheck(interval);
        }
        interval=Math.floor(seconds/ 2592000);
        if(ivterval>1){
            return interval+ 'Month'+this.pluralCheck(interval);
        }
        interval=Math.floor(seconds/ 86400);
        if(ivterval>1){
            return interval+ 'Day'+this.pluralCheck(interval);
        }
        interval=Math.floor(seconds/ 3600);
        if(ivterval>1){
            return interval+ 'Hour'+this.pluralCheck(interval);
        }
        interval=Math.floor(seconds/ 60);
        if(ivterval>1){
            return interval+ 'minute'+this.pluralCheck(interval);
        }
        return Math.floor(seconds)+ 'second'+this.pluralCheck(seconds);
    }

    componentDidMount = () => {
        var that =this;
        f.auth().onAuthStateChanged(function(user){
            if(user){
                //Logged in
                that.setState({
                    loggedin: true
                })
            }else{
                //not Logged in
                that.setState({
                    loggedin: false
                });
            }
        });
        this.checkParams();
    }
    
    render(){
        return(
            <View style={{flex:1}}>
                { this.state.loggedin == true ? (
                    //are logged in
                    <Text>comment</Text>
                ) : (
                    //not logged in
                    <View>
                        <Text>Please login to post the comment</Text>
                    </View>    
                )}

             
            </View>

        )
        
    }
}

export default comment;
