import React from 'react';
import {FlatList,StyleSheet,Text,View,Image} from 'react-native';

import { f,auth, database, storage } from '../../config/config.js';

class feed extends React.Component{

    constructor(props){
        super(props);
        this.state={
            photo_feed:[],
            refresh: false,
            loading:true
        }
    }
    componentDidMount = () => {

        //Load Feed
        this.loadFeed();
    }
    loadFeed = () => {
        this.setState({
            refresh:true,
            photo_feed:[]
        });
        var thst =this;

        database.ref('photos').orderByChild('posted').once('value').then(function(snapshot){
            const exists = (snapshot.val() !== null);
            if(exists) data =sanpshot.val();
                var photo_feed=thst.state.photo_feed;

                for (var photo in data){
                    var photoObj = data[photo];
                    database.ref('users').child(photoObj.author).once('value').then(function(snapshot){
                        const exists = (snapshot.val() !== null);
                        if(exists) data =snapshot.val();
                            photo_feed.push({
                                id:photo,
                                url:photoObj.url,
                                caption:photoObj.caption,
                                posted:photoObj.posted,
                                author: data.username
                            });

                            this.setState({
                                refresh:false,
                                loading: false
                            });
                }).catch(error => comsole.log(error));
            }
        }).catch(error => console.log(error));
    }
    loadNew =()=>{
         
        
        this.loadFeed();


        }
    render(){
        return(
            <View style={{flex:1}}>
                <View style={{height:70,padding:30,backgroundColor:'white',borderColor:'lightgray',borderBottomWidth:0.5,justifyContent:'center',alignItems:'center'}}>
                    <Text>FEED</Text>
                </View>
             
                <FlatList 
                    refreshing={this.state.refresh}
                    onRefresh={this.loadNew}
                    data={this.state.photo_feed}
                    keyExtractor={(item,index)=> index.toString()}
                    style={{flex:1,backgroundColor:'#eee'}}
                    renderItem={({item,index})=> (
                    <View key={index} style={{width:'100%',overflow:'hidden',marginBottom:5,justifyContent:'space-between',borderBottomWidth:1,borderColor:'grey'}}>
                            <View style ={{padding:5,width:'100%',flexDirection:'row',justifyContent:'space-between'}}>
                                <Text>Time</Text>
                                <Text>@Rusty</Text>
                            </View>
                        <View>
                            <Image
                                 source={{url:'https://source.unsplash.com/random/500x'+Math.floor(Math.random()) }}
                                 style ={{resizeMode:'cover',width:'100%',height:275}}
                                 />
                        </View>
                    <View style={{padding:5}}>
                        <Text>Caption text</Text>
                        <Text style={{marginTop:10,textAlign:'center'}}>View comments...</Text>
                    </View>
                    </View>
                )}
                />
            </View>    
        )
    }
}

export default feed ;
